/**
 * Initializes a new `AuthClient`, checks authentication state,
 * and executes the provided function if already authenticated.
 *
 * - Always creates a fresh `AuthClient` using {@link createAuthClient}.
 * - If the client is **not authenticated**, it resets the client via {@link safeCreateAuthClient}
 *   to ensure a clean session.
 * - If authenticated, it runs the given async function `fn`.
 *
 * @param {Object} params
 * @param {() => Promise<void>} params.fn - The asynchronous function to execute when authenticated.
 * @param {boolean} params.syncTabsOnSuccess - Broadcast the successful authentication to other tabs.
 *
 * @returns {Promise<void>} Resolves when authentication is handled and the provided function is executed (if applicable).
 */
export declare const authenticateWithAuthClient: ({ fn, syncTabsOnSuccess }: {
    fn: () => Promise<void>;
    syncTabsOnSuccess: boolean;
}) => Promise<void>;
/**
 * Creates a new `AuthClient` instance and checks authentication state,
 * then forwards the result to the provided callback function.
 *
 * Unlike {@link authenticateWithAuthClient}, this function does **not**
 * reuse or reset the existing `AuthClient`, and does **not** perform broadcast logic.
 * It simply ensures a fresh client is always created and reports whether it is authenticated.
 *
 * @param {Object} params
 * @param {(params: {authenticated: boolean}) => Promise<void>} params.fn
 *   The function to execute with the result of the authentication status.
 *
 * @returns {Promise<void>} Resolves once the callback has been invoked.
 */
export declare const authenticateWithNewAuthClient: ({ fn }: {
    fn: (params: {
        authenticated: boolean;
    }) => Promise<void>;
}) => Promise<void>;
